def makenew(Att,phere,emaxhere):
    nhere=Att.getSize()
    Dt=Vec()
    Att.getDiagonal(Dt)
    Dt.reciprocal()
    Dpt=PETSc.Mat().createAIJ([nhere, nhere])
    Dpt.setUp()
    Dpt.setDiagonal(Dt)
    Dpt.assemblyBegin()
    Dpt.assemblyEnd()
    omega=-4.0/3.0/emaxhere
    second=omega*Dpt*Att*phere
    tmat=phere+second
    return tmat

